sp_widget.client_script:
api.controller = function($scope, $interval, $location) {
    /* widget controller */
    var c = this;
    c.greeting = greetUser();
    c.formattedDate = getFormattedDate();
    c.goToLocationSelector = function() {
        var redirectObj = {
            id: "gqi_walkup_queue_location"
        };
        $location.search(redirectObj);
    };

    $interval(function() {
        $scope.currentTime = new Date().toLocaleTimeString();
    }, 1000);

    function getFormattedDate() {
        var daysOfWeek = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];
        var monthsOfYear = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];

        var now = new Date();
        var dayName = daysOfWeek[now.getDay()];
        var monthName = monthsOfYear[now.getMonth()];
        var dayOfMonth = now.getDate().toString().padStart(2, '0');

        return dayName + ', ' + monthName + ' ' + dayOfMonth;

    }

    function greetUser() {
        var today = new Date(),
            greet;
        var hrs = today.getHours();
        if (hrs < 12) {
            greet = 'Good Morning!';
        } else if (hrs >= 12 && hrs < 17) {
            greet = 'Good Afternoon!';
        } else if (hrs >= 17 && hrs < 24) {
            greet = 'Good Evening!';
        }
        return greet;
    }
};

