sys_ws_operation.operation_script:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    var pathParams = request.pathParams;
    var queryParams = request.queryParams;

    var locationID = pathParams.locationID;
    var state = queryParams.state;
    var includeAppointments = queryParams.includeAppointments;
    var limit = queryParams.limit;

    if (!locationID || !state || !includeAppointments) {
        gs.error('Some error occured while fetching users in an onsite queue');
        return;
    }
    var api = new LTU_WalkUpUtil().fetchOnsiteQueue(locationID, state, includeAppointments, limit);
    var apiResponse = JSON.parse(api.Response);
    if (apiResponse != null)
        return apiResponse.result;
    else
        return apiResponse;

})(request, response);
