sys_ws_operation.operation_script:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    // implement resource here
    try {
        var pathParms = request.pathParams;
        var baseUrl = 'https://' + gs.getProperty('instance_name') + '.service-now.com';
        var url = baseUrl + '/api/sn_walkup/walk_up/queue/partitioned/' +
            pathParms.locationId +
            '?queueState=' + pathParms.queueState +
            '&appointments=' + pathParms.appointmentIncluded;

        var appointmentRestApi = new sn_ws.RESTMessageV2();
        appointmentRestApi.setHttpMethod("get");
        appointmentRestApi.setRequestHeader('Accept', 'application/json');
        appointmentRestApi.setAuthenticationProfile('basic', gs.getProperty('x_80404_ltu_walkup.walkup_auth_profile'));
        appointmentRestApi.setEndpoint(url);
        var apiResponse = appointmentRestApi.execute();

        var responseBody = apiResponse.getBody();
        var httpStatus = apiResponse.getStatusCode();
        if (httpStatus == "200") {
            var parsedResponse = JSON.parse(responseBody)
            return parsedResponse.result;
        } else
            return null;
    } catch (ex) {
        gs.info("Error while to fetch list of users waiting in the queue (Scripted Rest Api): " + ex.message);
        return {
            "Received": false,
            "Response": null
        };
    }
})(request, response);
